package corejava;

public class Do_While {
	
	   static int i=1;
	   
	   public static void printmessage() {
		   
		      do  {
		    	  
		    	  System.out.println("the value of i is="+i);
		    	  System.out.println("Hello world");
		    	  i++; // 2
		      }  while(i<1);
	   }

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		printmessage();
	}

}
