package corejava;

public class For_loop {
	
	public static void printmessage() {
		
		for(int i=0;i<=10;i++) {
			
			System.out.println("the value of i is="+i);
			
			System.out.println("Hello world");
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		printmessage();
	}

}
